// Prepare contact data for AI analysis
const contacts = items[0].json.data.contacts || [];
const currentDate = new Date();
const thirtyDaysAgo = new Date(currentDate.getTime() - (30 * 24 * 60 * 60 * 1000));
const ninetyDaysAgo = new Date(currentDate.getTime() - (90 * 24 * 60 * 60 * 1000));

// Prepare contact data for AI analysis
const contactData = contacts.map(contact => {
  const lastEngagement = contact.last_engagement_date ? new Date(contact.last_engagement_date) : null;
  const signupDate = contact.created_at ? new Date(contact.created_at) : null;
  const lastOrderDate = contact.last_order_date ? new Date(contact.last_order_date) : null;

  // Calculate engagement metrics
  const daysSinceEngagement = lastEngagement ? Math.floor((currentDate - lastEngagement) / (1000 * 60 * 60 * 24)) : 999;
  const daysSinceSignup = signupDate ? Math.floor((currentDate - signupDate) / (1000 * 60 * 60 * 24)) : 0;
  const daysSinceLastOrder = lastOrderDate ? Math.floor((currentDate - lastOrderDate) / (1000 * 60 * 60 * 24)) : 999;

  // Calculate engagement score
  let engagementScore = 100;
  if (daysSinceEngagement > 90) engagementScore -= 50;
  else if (daysSinceEngagement > 30) engagementScore -= 30;
  else if (daysSinceEngagement > 7) engagementScore -= 10;

  const emailOpenRate = contact.email_open_rate || 0;
  const emailClickRate = contact.email_click_rate || 0;

  if (emailOpenRate < 0.1) engagementScore -= 20;
  else if (emailOpenRate < 0.2) engagementScore -= 10;

  if (emailClickRate < 0.02) engagementScore -= 15;
  else if (emailClickRate < 0.05) engagementScore -= 8;

  return {
    id: contact.id,
    email: contact.email,
    first_name: contact.first_name,
    last_name: contact.last_name,
    age: contact.age || null,
    location: contact.location || null,
    interests: contact.interests || [],
    lifetime_value: contact.lifetime_value || 0,
    order_count: contact.order_count || 0,
    last_order_date: contact.last_order_date,
    days_since_engagement: daysSinceEngagement,
    days_since_signup: daysSinceSignup,
    days_since_last_order: daysSinceLastOrder,
    email_open_rate: emailOpenRate,
    email_click_rate: emailClickRate,
    engagement_score: Math.max(0, engagementScore),
    signup_source: contact.signup_source || 'unknown',
    customer_tier: contact.customer_tier || 'standard',
    preferred_content_type: contact.preferred_content_type || 'email',
    timezone: contact.timezone || 'UTC'
  };
});

// Group contacts for analysis
const analysisData = {
  total_contacts: contactData.length,
  contacts: contactData,
  analysis_date: currentDate.toISOString().split('T')[0],
  time_ranges: {
    thirty_days_ago: thirtyDaysAgo.toISOString().split('T')[0],
    ninety_days_ago: ninetyDaysAgo.toISOString().split('T')[0]
  }
};

return [{
  json: analysisData
}];

// Process AI analysis results and create segments
const contactData = items[0].json;
const aiResponse = items[1].json.choices[0].message.content;

let analysisResult;
try {
  analysisResult = JSON.parse(aiResponse);
} catch (error) {
  // Fallback if AI response is not valid JSON
  analysisResult = {
    segments: [
      {
        name: "High Value Customers",
        description: "Customers with high lifetime value and engagement",
        criteria: { lifetime_value: { min: 1000 }, engagement_score: { min: 70 } },
        contacts: [],
        size: 0,
        characteristics: ["High spending", "Active engagement", "Loyal customers"]
      },
      {
        name: "At Risk Customers",
        description: "Customers showing signs of disengagement",
        criteria: { days_since_engagement: { min: 30 }, engagement_score: { max: 40 } },
        contacts: [],
        size: 0,
        characteristics: ["Low engagement", "Inactive recently", "Potential churn"]
      },
      {
        name: "New Customers",
        description: "Recently acquired customers",
        criteria: { days_since_signup: { max: 30 } },
        contacts: [],
        size: 0,
        characteristics: ["Recent signups", "Onboarding phase", "High potential"]
      }
    ],
    insights: ["Contact data analysis completed"],
    recommendations: ["Review segments and implement targeted campaigns"]
  };
}

// Process each segment and identify matching contacts
const processedSegments = analysisResult.segments.map(segment => {
  const matchingContacts = contactData.contacts.filter(contact => {
    // Apply segment criteria (simplified matching)
    if (segment.criteria.lifetime_value?.min && contact.lifetime_value < segment.criteria.lifetime_value.min) return false;
    if (segment.criteria.lifetime_value?.max && contact.lifetime_value > segment.criteria.lifetime_value.max) return false;
    if (segment.criteria.engagement_score?.min && contact.engagement_score < segment.criteria.engagement_score.min) return false;
    if (segment.criteria.engagement_score?.max && contact.engagement_score > segment.criteria.engagement_score.max) return false;
    if (segment.criteria.days_since_engagement?.min && contact.days_since_engagement < segment.criteria.days_since_engagement.min) return false;
    if (segment.criteria.days_since_engagement?.max && contact.days_since_engagement > segment.criteria.days_since_engagement.max) return false;
    if (segment.criteria.days_since_signup?.min && contact.days_since_signup < segment.criteria.days_since_signup.min) return false;
    if (segment.criteria.days_since_signup?.max && contact.days_since_signup > segment.criteria.days_since_signup.max) return false;
    return true;
  });

  return {
    name: segment.name,
    description: segment.description,
    criteria: segment.criteria,
    contacts: matchingContacts.map(c => c.id),
    size: matchingContacts.length,
    characteristics: segment.characteristics,
    avg_lifetime_value: matchingContacts.length > 0 ? 
      (matchingContacts.reduce((sum, c) => sum + c.lifetime_value, 0) / matchingContacts.length).toFixed(2) : 0,
    avg_engagement_score: matchingContacts.length > 0 ? 
      (matchingContacts.reduce((sum, c) => sum + c.engagement_score, 0) / matchingContacts.length).toFixed(2) : 0
  };
});

return [{
  json: {
    analysis_date: contactData.analysis_date,
    total_contacts: contactData.total_contacts,
    segments: processedSegments,
    insights: analysisResult.insights || [],
    recommendations: analysisResult.recommendations || [],
    generated_at: new Date().toISOString()
  }
}];

// Create BayEngage lists for each segment
const data = items[0].json;
const segments = data.segments;

// Create list creation tasks for each segment
const listCreationTasks = segments.map(segment => ({
  segment_name: segment.name,
  segment_description: segment.description,
  contact_count: segment.size,
  contact_ids: segment.contacts,
  avg_lifetime_value: segment.avg_lifetime_value,
  avg_engagement_score: segment.avg_engagement_score,
  characteristics: segment.characteristics
}));

return listCreationTasks.map(task => ({ json: task }));

// Add contacts to the newly created list
const listData = items[0].json;
const segmentData = items[1].json;

// Add each contact to the list
const addContactTasks = segmentData.contact_ids.map(contactId => ({
  list_id: listData.id,
  contact_id: contactId,
  segment_name: segmentData.segment_name,
  segment_description: segmentData.segment_description
}));

return addContactTasks.map(task => ({ json: task }));

// Aggregate segment creation results
const segmentData = items[0].json;
const addResults = items[1].json;

// Count successful additions
const successfulAdditions = addResults.filter(result => result.success !== false).length;
const totalContacts = segmentData.contact_ids.length;

return [{
  json: {
    segment_name: segmentData.segment_name,
    segment_description: segmentData.segment_description,
    list_id: segmentData.list_id,
    total_contacts: totalContacts,
    successful_additions: successfulAdditions,
    failed_additions: totalContacts - successfulAdditions,
    success_rate: totalContacts > 0 ? ((successfulAdditions / totalContacts) * 100).toFixed(2) : 0,
    avg_lifetime_value: segmentData.avg_lifetime_value,
    avg_engagement_score: segmentData.avg_engagement_score,
    characteristics: segmentData.characteristics
  }
}];

