// Analyze contact data to determine VIP status
const contactData = items[0].json.data;
const currentDate = new Date();

// Calculate VIP score based on multiple factors
let vipScore = 0;
let vipTier = 'standard';
let vipBenefits = [];

// Lifetime value factor (40% weight)
const lifetimeValue = contactData.lifetime_value || 0;
if (lifetimeValue >= 10000) {
  vipScore += 40;
  vipTier = 'platinum';
  vipBenefits = ['Priority Support', 'Exclusive Events', 'Personal Account Manager', 'Free Shipping', 'Early Access'];
} else if (lifetimeValue >= 5000) {
  vipScore += 30;
  vipTier = 'gold';
  vipBenefits = ['Priority Support', 'Exclusive Events', 'Free Shipping', 'Early Access'];
} else if (lifetimeValue >= 2000) {
  vipScore += 20;
  vipTier = 'silver';
  vipBenefits = ['Priority Support', 'Free Shipping', 'Early Access'];
}

// Order frequency factor (25% weight)
const orderCount = contactData.order_count || 0;
if (orderCount >= 20) {
  vipScore += 25;
} else if (orderCount >= 10) {
  vipScore += 15;
} else if (orderCount >= 5) {
  vipScore += 10;
}

// Engagement factor (20% weight)
const emailOpenRate = contactData.email_open_rate || 0;
const emailClickRate = contactData.email_click_rate || 0;
if (emailOpenRate >= 0.3 && emailClickRate >= 0.05) {
  vipScore += 20;
} else if (emailOpenRate >= 0.2 && emailClickRate >= 0.03) {
  vipScore += 15;
} else if (emailOpenRate >= 0.1 && emailClickRate >= 0.02) {
  vipScore += 10;
}

// Recency factor (15% weight)
const lastOrderDate = contactData.last_order_date ? new Date(contactData.last_order_date) : null;
if (lastOrderDate) {
  const daysSinceLastOrder = Math.floor((currentDate - lastOrderDate) / (1000 * 60 * 60 * 24));
  if (daysSinceLastOrder <= 30) {
    vipScore += 15;
  } else if (daysSinceLastOrder <= 90) {
    vipScore += 10;
  } else if (daysSinceLastOrder <= 180) {
    vipScore += 5;
  }
}

// Determine if this is a new VIP or status change
const previousVipTier = contactData.previous_vip_tier || 'standard';
const isNewVip = vipTier !== 'standard' && previousVipTier === 'standard';
const isUpgrade = vipTier !== previousVipTier && vipTier !== 'standard' && previousVipTier !== 'standard';
const isDowngrade = vipTier === 'standard' && previousVipTier !== 'standard';

// Generate personalized VIP content
const vipContent = {
  tier: vipTier,
  score: vipScore,
  benefits: vipBenefits,
  is_new_vip: isNewVip,
  is_upgrade: isUpgrade,
  is_downgrade: isDowngrade,
  previous_tier: previousVipTier,
  lifetime_value: lifetimeValue,
  order_count: orderCount,
  email_open_rate: emailOpenRate,
  email_click_rate: emailClickRate
};

return [{
  json: {
    contact_id: contactData.id,
    first_name: contactData.first_name,
    last_name: contactData.last_name,
    email: contactData.email,
    phone: contactData.phone,
    vip_content: vipContent,
    analysis_date: currentDate.toISOString().split('T')[0]
  }
}];

